import { useState } from "react";
import Cart from "./Cart";
import WishList from "./WishList";
import "./Main.css"

function Main(){

  const products = [
  {
    id: 1,
    img: "https://encrypted-tbn1.gstatic.com/shopping?q=tbn:ANd9GcSUczHznI3hoT7XbFyZLr4FqMYTpzBe1CLPacKzn_HljwVKk-GhKGkrDyp3isRgNke6e1IxJfN37KRCW0NM9pUGg1VE2LfNSOVhUL7nnkbVhIBq95ER-aXU752Y",
    name: "Watches for Men"
  },
  {
    id: 2,
    img: "https://m.media-amazon.com/images/I/71eUwDk8z+L._AC_UL480_FMwebp_QL65_.jpg",
    name: "Titan Brown Leather Watch"
  },
  {
    id: 3,
    img: "data:image/jpeg;base64,",
    name: "Fossil Chronograph Black Watch"
  },
  {
    id: 4,
    img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTFO9rBCBjHkS4p1hw_Dm9GBFGZaxBvwCBwlQ&s",
    name: "Casio Vintage Gold Watch"
  },
  {
    id: 5,
    img: "data:image/jpeg;base64,",
    name: "Boat Smart Watch - Blue"
  },
  {
    id: 6,
    img: "data:image/jpeg;base64,",
    name: "Noise ColorFit Pro Smart Watch"
  },
  {
    id: 7,
    img: "data:image/jpeg;base64,",
    name: "Rolex Day-Date Premium Watch"
  },
  {
    id: 8,
    img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTUOOaybfiLCdl41uTj0aSBWOy-J6IOuHE8_w&s",
    name: "Sonata Classic Silver Watch"
  },
  {
    id: 9,
    img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRZbHcTSvAuFQHnw7p8Pr-wG7uzmDALQTXBAA&s",
    name: "Fastrack Trendy Black Watch"
  },
  {
    id: 10,
    img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRZbHcTSvAuFQHnw7p8Pr-wG7uzmDALQTXBAA&s",
    name: "Timex Expedition Analog Watch"
  }
];


 const [view,setView]=useState("products");
 const [cart,AddCart]=useState([]);
  

  const [wishlist,AddWishlist]=useState([]);
  

  const Add_cart=(id, product)=>{
    if (cart.some((item) => item.id === id)) {
    AddCart(
      cart.map((item) =>
        item.id === id
          ? { ...item, count: item.count + 1 }   
          : item
      )
    );
  } else {
    AddCart([...cart, { ...product, count: 1 }]);  
  }
  }

const Add_wish=(i,e)=>{
    
    if(wishlist.some((x)=>x.id!=i)||wishlist.length==0){
      AddWishlist([...wishlist, { ...e, count: 1 }]);  
  }
}

  return(
    <>
    <button onClick={()=>setView("cart")}>Cart</button>
    <button onClick={()=>setView("wishlist")}>WishList</button>
    <button onClick={()=>setView("products")}>products</button>
    <div className="one">
    {view=="cart" && <Cart cart={cart}/> }
    {view=="wishlist" && <WishList wish={wishlist}/>}
    {view=="products" && products.map((e)=>(
      <div key={e.id} className="prod">
        <img src={e.img} alt="Watch Image"/>
        <p>{e.name}</p>
        <button onClick={()=>Add_cart(e.id,e)}>Add to Cart</button>
        <button onClick={()=>Add_wish(e.id,e)}>Add to WishList</button>
      </div>
      
    )
    )}
    </div>
    </>
  );
}

export default Main;